sys_script_include.script:
var cardUtils = Class.create();
cardUtils.prototype = {
	initialize: function() {
	},

	getAddress: function(){

		var user = gs.getUser();
		var id = user.getID();
		var grUser = new GlideRecord('sys_user');
		grUser.get(id);

		//gs.info('CoiUtils2 - getManager()' + 'user = ' + id + 'manager =' + manager);
		var address = '';
		address = grUser.street + " " + grUser.state +  " " + grUser.zip;

		//gs.info ('cardUtils address' + address);

		return address;
	},


	getPhone: function(){

		var user = gs.getUser();
		var id = user.getID();
		var grUser = new GlideRecord('sys_user');
		grUser.get(id);

		//gs.info('CoiUtils2 - getManager()' + 'user = ' + id + 'manager =' + manager);
		var phone = grUser.phone;

		//gs.info ('cardUtils phone' + phone);

		return phone;
	},
	type: 'cardUtils'
};
